import { Component } from '@angular/core';

//TODO: Implement 
@Component({
  selector: 'app-delete-page',
  templateUrl: './delete-page.component.html',
  styleUrls: ['./delete-page.component.css']
})
export class DeletePageComponent {
  public title: string = "";

  public constructor(private service: DataService) {

  }

  public onTitleChange(e: Event) {
    this.title = (<HTMLInputElement>e.target).value;
  }

  public onSubmit(e: Event) {
    e.preventDefault();
    this.service.delete(this.title).subscribe(
      (k) => alert("Success!"),
      (error) => alert("Can't insert the data")
    );
  }
}
